def host = hostProps.get("system.hostname")
def timeout = 1000

list = hostProps.get("custom.ssl_listening_ports") ?: ""

instances = [:]

list.tokenize(",").each{inst->
    instances[inst.tokenize("|")[0]] = inst.tokenize("|")[1]
}

instances.each{ port, name ->
	try {
		def socket = new Socket()
		socket.connect(new InetSocketAddress(host, 445), timeout)
		println "${host}:${port}##${name}######auto.ssl.port=${port}"
		socket.close()
	}
	catch (e) {} // Port isn't open
}


return 0

