// We have to be careful with imports, as javax.net.ssl and apache conflict.
import org.apache.http.conn.ssl.SSLSocketFactory

import javax.net.ssl.SSLContext
import javax.net.ssl.TrustManager
import javax.net.ssl.X509TrustManager
import java.security.Security
import java.security.cert.CertificateException
import java.security.cert.X509Certificate

// To run in debug mode, set to true
Boolean debug = false

def ips = hostProps.get("system.ips") ? hostProps.get("system.ips").split(",") : []
def wildValue = instanceProps.get("wildvalue")
def port = instanceProps.get("auto.ssl.port")
def host = wildValue // Assume the wildvalue is just a host name initially
def timeout = 10000

// Reassign host and port based on whether last value in split could be a port
def wildValueTokens = wildValue.split(":")
if (wildValueTokens.last().isInteger()) {
	host = wildValue[0..wildValue.lastIndexOf(':') - 1]
	port = wildValueTokens.last()
}

def modLoader = new GroovyShell(getBinding()).parse(com.logicmonitor.mod.Snippets.getLoader())
def lmEmit = modLoader.load("lm.emit", "0")   // Load LM emit module

LMDebugPrint("- DEBUG MODE ON -\n\tHost: ${host}\n\tPort: ${port}", debug)

def states = [
    "UnrecognizedName"		   :  0,
    "Valid"					   :  0,
    "HostNameMismatch"		   : -1,
    "TimestampCheckFailed"	   : -1,
    "SelfSignedCertificate"	   : -1,
    "SelfIssuedCertificate"	   : -1,
    "InvalidCertificationPath" : -1,
    "ConnectionReset"		   : -1,
    "UnknownHostIPFallback"	   : -1,
    "OtherException"		   : -1
]

def certificates = []
def alreadyReported = false

// We store the socket factory so it can be reused
def sslSocketFactory

// Before we run the above, we specify these properies to prevent some auth types
System.setProperty(  "jdk.tls.disabledAlgorithms", "SSLv3, DH keySize < 768")
Security.setProperty("jdk.tls.disabledAlgorithms", "SSLv3, DH keySize < 768")

if (!loadCerts(host, port, sslSocketFactory, certificates, alreadyReported, timeout, debug)) {
	// Something has gone wrong. Try the IP instead of the hostname.
	ips.any{ ip ->
        LMDebugPrint("Attempting fallback with IP - ${ip}", debug)
		if (loadCerts(ip, port, sslSocketFactory, certificates, alreadyReported, timeout, debug)) {
			// If we get a successful run, don't process the other IPs
			return true
		}
	}
}

def selfSigned = false
def selfIssued = false

if (certificates) {
    // Get the current time and add 100 years as a base expiry time
    def time = Calendar.getInstance()
    time.add(1, 100)
    def expire = time.getTime()

	// Get epoch time to use as a base issue time
	time.setTimeInMillis(0)
	issue = time.getTime()

	certificates.eachWithIndex{ cert, index ->
		// Is the issuer the same as the subject?
		if (cert.getIssuerDN() == cert.getSubjectDN()) {
			// Yes! This cert is self issued or self signed.
			selfIssued = cert.isSelfIssued(cert)
			selfSigned = cert.isSelfSigned(cert, null)
		}

		LMDebugPrint("\tCertification ${index} [Type: ${cert.getType()}]", debug)
		LMDebugPrint("\t\tIssue Date: ${cert.getNotBefore()}, Expiration Date: ${cert.getNotAfter()}", debug)

		// Is the certificate time after the issue time?
		if (cert.getNotBefore().after(issue)) {
			// Update the issue time to the certificate's time.
			issue = cert.getNotBefore()
		}

		// Is the certificate time before the expiry time?
		if (cert.getNotAfter().before(expire)) {
			// Update the expiry time to the certificate's time.
			expire = cert.getNotAfter()
		}
	}

	def now = Calendar.getInstance().getTime()
	long currentTime = now.getTime()
	long expireTime = expire.getTime()

	lmEmit.dp("ChainLength", certificates.size())
	lmEmit.dp("TimeRemaining", ((expireTime - currentTime) / 1000 / 60 / 60 / 24))
}

// At this point we have all the time based information.
// We can start a new connection with validation enabled to get validation info.
try {
	// Create a new ssl socket factory with a default context and strict verification (similar to IE6+ internally).
	def newSslSocketFactory = new SSLSocketFactory(SSLContext.getDefault(), SSLSocketFactory.STRICT_HOSTNAME_VERIFIER)
	def socket = new Socket(host, port.toInteger())
	def sslsocket = newSslSocketFactory.createSocket(socket, host, port.toInteger(), true)

	// Specify some common ciphers and enable them on our ssl socket
	def ciphers = new HashSet<String>()
	ciphers.addAll(Arrays.asList(sslsocket.getEnabledCipherSuites()))
	ciphers.add("SSL_RSA_WITH_RC4_128_SHA")
	ciphers.add("SSL_RSA_WITH_RC4_128_MD5")
	sslsocket.setEnabledCipherSuites(ciphers.toArray(new String[ciphers.size()]))

	// Begin a handshake. At this point we will find out if we have an exception.
	sslsocket.startHandshake()

    // If we didn't throw, then this is a valid cert
	states["Valid"] = 1

    // Reassign values for states with default -1 values; valid cert indicates there are no issues
	states.each{ k,v->
		if(v == -1) {
			states[k] = 0
		}
	}
}
catch (Exception e) { // The certificate is invalid!
	LMDebugPrint("\n\tMessage: ${e.message}\n", debug)

    // Make note of issues we can identify.  Leave others we can't verify as -1 to indicate issue not explicitly discovered but could exist.
	if (e.message.contains("doesn't match")) {
		states["HostNameMismatch"] = 1
		alreadyReported = true
	}

    if (e.toString().contains("UnknownHostException")) {
		states["UnknownHostIPFallback"] = 1
		alreadyReported = true
	}

	if (e.message.contains("timestamp check failed")) {
		states["TimestampCheckFailed"] = 1
		alreadyReported = true
	}

	if (e.message.contains("Connection reset")) {
		states["ConnectionReset"] = 1
		alreadyReported = true
	}

	if (e.message.contains("valid certification path")) {
		// This is an error further down the path (such as an invalid root or self-sign)
		if (selfSigned) {
			states["SelfSignedCertificate"] = 1
		}
        else {
			if (selfIssued) {
				states["SelfIssuedCertificate"] = 1
			}
            else {
				states["InvalidCertificationPath"] = 1
			}
		}
		alreadyReported = true
	}

    // Make note of unknown exceptions and print to debug
	if (!alreadyReported) {
		states["OtherException"] = 1
		LMDebugPrint("\n\tThis issue was detected but doesn't have a known solution; reporting as 'Other'", debug)
	}
}

states.each{ k,v->
	lmEmit.dp(k, v)
}

return 0


/**
 * Helper function to print out debug messages for troubleshooting purposes.
 */
void LMDebugPrint(message, Boolean debug=false) {
    if (debug) {
        println(message.toString())
    }
}


/**
 * This custom trust manager class allows all certificates, but extracts them as it does so.
 */
class LMEmptyX509TrustManager implements TrustManager, X509TrustManager {
	private def managerLog
	private def globalCerts

	private LMEmptyX509TrustManager(_managerLog, _globalCerts) {
		managerLog = _managerLog
		globalCerts = _globalCerts
	}

	// Define all X509TrustManager functions to not fail validation on expire.
	@Override
	X509Certificate[] getAcceptedIssuers() {
		managerLog.push("\tTrustManager: getAcceptedIssuers called.")
        X509Certificate[] emptyList = []

		return emptyList
	}

	@Override
	void checkServerTrusted(X509Certificate[] certs, String authType) throws CertificateException {
		managerLog.push("\tTrustManager: checkServerTrusted got ${(certs == null ? 0 : certs.length)} certs. Auth type: ${authType}")

		// Instead of checking if the cert is trusted...save it to our global storage!
		if (certs) {
			globalCerts.clear()
			globalCerts.addAll(certs)
		}
	}

	@Override
	void checkClientTrusted(X509Certificate[] certs, String authType) throws CertificateException {
		managerLog.push("\tTrustManager: checkClientTrusted got ${(certs == null ? 0 : certs.length)} certs. Auth type: ${authType}")
	}
}


/**
 * Gather certificates from a host and port, and put them into global storage.
 */
def loadCerts(host, port, sslSocketFactory, certificates, alreadyReported, timeout, debug=false, log=[]) {
	def sslSock = null
    def success = false

	// Writing to sockets has a chance of failure, so lets try/catch.
	try {
        // Create a socket factory - if it doesn't already exist - using custom trust manager to trust all certs
		if (sslSocketFactory == null) {
			def trustManager = new LMEmptyX509TrustManager(log, certificates)

			// In the case of TLSv1.2, we want to use our custom trust manager
			def context = SSLContext.getInstance("TLSv1.2")
			context.init(null, [trustManager] as TrustManager[], null)

			sslSocketFactory = context.getSocketFactory()
		}

		def sock = new Socket()
		sock.connect(new InetSocketAddress(host, port.toInteger()), timeout)
		sslSock = sslSocketFactory.createSocket(sock, host, port.toInteger(), true)

		// Specify some common ciphers and enable them on our SSL socket
		def ciphers = new HashSet<String>()
		ciphers.addAll(Arrays.asList(sslSock.getEnabledCipherSuites()))
		ciphers.add("SSL_RSA_WITH_RC4_128_SHA")
		ciphers.add("SSL_RSA_WITH_RC4_128_MD5")
		sslSock.setEnabledCipherSuites(ciphers.toArray(new String[ciphers.size()]))

		// Make a request to send. The text doesnt seem to matter as we don't care about the response.
		def request = "GET / HTTP/1.1\r\nHost:${host}:${port}\r\nConnection:Close\r\nUser-Agent:SSLClient/1.0\r\n\r\n"

		// Create a writer we will use to send our request.
		def writer = new OutputStreamWriter(sslSock.getOutputStream(), "utf-8")
		LMDebugPrint("\nTry to send request to server...", debug)

		// Write the request to the output stream.
		writer.write(request)
		LMDebugPrint("\tRequest sent...", debug)

		// Make sure the output stream has consumed our request.
		writer.flush()
		LMDebugPrint("\tRequest flushed...\n\tSUCCESS", debug)

        success = true
	}
	catch (Exception e) {
		// Something went wrong! Will be investigated later in the script.
		LMDebugPrint("\tSocket Error: ${e}", debug)

        if (e.message.contains("unrecognized_name") && !alreadyReported) {
            states["UnrecognizedName"] = 1
            alreadyReported = true
        }
	}

	// If the ssl sock isn't closed already, we need to do it!
	if (sslSock != null) {
		sslSock.close()
	}

    // Print log info from trust manager class
    if (debug && log) {
        println "\n${log.join('\n')}"
    }

	return success
}
